package TestDemo;

public class SwitchCaseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int choice = 2;
		switch(choice) {
			case 1: System.out.println("The chosen one");
			case 2: System.out.println("Double trouble");
			case 3: System.out.println("The three Musketeers");
			default: System.out.println("Wrong input");
	}
	}
}
